INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('A70', '7'::smallint, 'A', 'Tuberkulose', 'A15.0 *|||* A15.1 *|||* A15.2 *|||* A15.3 *|||* A15.4 *|||* A15.5 *|||* A15.6 *|||* A15.7 *|||* A15.8 *|||* A15.9 *|||* A16.0 *|||* A16.1 *|||* A16.2 *|||* A16.3 *|||* A16.4 *|||* A16.5 *|||* A16.7 *|||* A16.8 *|||* A16.9 *|||* A17.0 *|||* A17.1 *|||* A17.8 *|||* A17.9 *|||* A18.0 *|||* A18.1 *|||* A18.2 *|||* A18.3 *|||* A18.4 *|||* A18.5 *|||* A18.6 *|||* A18.7 *|||* A18.8 *|||* A19.0 *|||* A19.1 *|||* A19.2 *|||* A19.8 *|||* A19.9 *|||* B90.0 *|||* B90.1 *|||* B90.2 *|||* B90.8 *|||* B90.9', 'Tuberkuloseinfektion an jeglicher Körperstelle *|||* Spätfolgen einer Tuberkulose', NULL, 'Konversion zu positivem Tuberkulintest, oder Nachweis von Mycobacterium tuberculosis bzw. M. bovis bei Mikroskopie bzw. Kultur, oder charakteristisches Erscheinungsbild bei Thoraxröntgen, oder charakteristisches histologisches Erscheinungsbild bei Biopsie', NULL, 'Fieber; A03 *|||* Husten; R05');
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('A71', '7'::smallint, 'A', 'Masern', 'B05.0 *|||* B05.1 *|||* B05.2 *|||* B05.3 *|||* B05.4 *|||* B05.8 *|||* B05.9', 'Spätfolgen von Masern', NULL, 'Prodromalstadium mit Bindehautentzündung, Fieber und Husten, plus weiße Flecken auf rotem Grund in der Wangenschleimhaut (Koplick-Flecken), oder konfluierender makulopapulöser Ausschlag, der sich über Gesicht und Körper ausbreitet, oder ein atypisches Exanthem bei einer teilweise immunisierten Person während einer akuten Masernerkrankung', NULL, 'Fieber; A03 *|||* anderes virales Exanthem; A76 *|||* generalisierter Ausschlag; S07');
