INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('K71', '7'::smallint, 'K', 'Rheumatisches Fieber/Herzerkrankung', 'I00 *|||* I01.0 *|||* I01.1 *|||* I01.2 *|||* I01.8 *|||* I01.9 *|||* I02.0 *|||* I02.9 *|||* I05.0 *|||* I05.1 *|||* I05.2 *|||* I05.8 *|||* I05.9 *|||* I06.0 *|||* I06.1 *|||* I06.2 *|||* I06.8 *|||* I06.9 *|||* I07.0 *|||* I07.1 *|||* I07.2 *|||* I07.8 *|||* I07.9 *|||* I09.0 *|||* I09.1 *|||* I09.2 *|||* I09.8 *|||* I09.9', 'Chorea Sydenham *|||* Mitralstenose', NULL, '- Für akutes rheumatisches Fieber: zwei Symptome erster Ordnung; oder ein Symptom erster Ordnung und zwei Symptome zweiter Ordnung, plus Nachweis einer vorangegangenen Streptokokkeninfektion; Symptome erster Ordnung: Polyarthritis migrans; Karditis; Chorea Sydenham; Erythema marginatum; subkutane Knotenbildung jüngeren Datums/Erythemna  nodosum; Symptome zweiter Ordnung: Fieber, Gelenkschmerzen, erhöhte BSR oder Nachweis von C-reaktivem Protein, verlängertes P-R Intervall im EKG. Für chronisch rheumatische Herzerkrankung: entweder körperliche Befunde, die für eine krankhafte Veränderung der Herzklappen oder des Endokards sprechen, bei einem Patienten mit rheumatischem Fieber in der Anamnese; oder körperliche Befunde, die für eine Mitralstenose sprechen, auch bei Fehlen von rheumatischem Fieber in der Anamnese, wenn keine andere Ursache nachweisbar ist', NULL, 'Herzklappenerkrankung; K83 *|||* andere Herzerkrankung; K84');
