INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('N90', '7'::smallint, 'N', 'Clusterkopfschmerz', 'G44.0', NULL, 'Migräne; N89', 'Anfälle von heftigen, oft quälenden einseitigen Schmerzen im periorbitalen und temporalen Bereich, die bis zu achtmal täglich auftreten, begleitet von konjunktivaler Injektion, Tränenbildung, Nasenverstopfung, Nasenrinnen, Schwitzen, Miose, Ptose oder periorbitalem Ödem. Die Anfälle treten gehäuft in Wochen oder Monate dauernden Perioden auf, zwischen denen Monate oder Jahre der Remission liegen', NULL, NULL);
