INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P01', '1'::smallint, 'P', 'Gefühl ängstlich/nervös/angespannt', 'R45.0', 'Angstzustand NB *|||* Angstgefühl', 'Angststörung; P74', 'Vom Patienten als Folge einer emotionalen oder psychosozialen Erfahrung berichtete Gefühle, wobei die Erfahrung nicht auf das Vorhandensein einer psychischen Störung zurückgeführt wird. Es gibt einen allmählichen Übergang von Gefühlen, die nicht willkommen, aber ganz normal sind, zu Gefühlen, die den Patienten so beunruhigen, dass er professionelle Hilfe sucht', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P02', '1'::smallint, 'P', 'akute Stressreaktion', 'F43.0 *|||* F43.2 *|||* F43.8 *|||* F43.9', 'Anpassungsstörung *|||* Kulturschock *|||* Stress/Trauer/Heimweh *|||* unmittelbares post-traumatische Stresstörung *|||* Schock (psychisch)', 'depressive Verstimmung; P03 *|||* depressive Störung; P76 *|||* post-traumatische Stresstörung; P82', 'Eine Reaktion auf ein mit Streß verbundenes Ereignis im Leben oder auf eine bedeutende Veränderung des Lebens, die eine nachhaltige Anpassung erfordert; entweder als erwartete Reaktion auf das Ereignis oder als Resultat mkangelhafter Anpassung, so dass die Bewährung im Alltag behindert und die soziale Funktionalität eingeschränkt wird, mit Wiedergewinnung des seelischen Gleichgewichts innerhalb einer begrenzten Zeitspanne', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P03', '1'::smallint, 'P', 'depressives Gefühl', 'R45.2 *|||* R45.3', 'Gefühl der Unzulänglichkeit *|||* Unglücklichsein *|||* Besorgnis', 'geringes Selbstvertrauen; P28 *|||* depressive Störung; P76', 'Vom Patienten als Folge einer emotionalen oder psychischen Erfahrung berichtete Gefühle, wobei die Erfahrung nicht auf eine psychische Störung zurückgeführt wird. Es gibt einen allmählichen Übergang von Gefühlen, die nicht willkommen, aber ganz normal sind, zu Gefühlen, die den Patienten so beunruhigen, dass er professionelle Hilfe sucht', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P04', '1'::smallint, 'P', 'reizbares/ärgerliches Gefühl/Verhalten', 'R45.1 *|||* R45.4 *|||* R45.5 *|||* R45.6', 'Agitiertheit NNB *|||* Rastlosigkeit NNB', 'hyperaktives Kind; P22 *|||* Reizbarkeit des Partners; Z13', 'Vom Patienten als Folge einer emotionalen oder psychischen Erfahrung berichtete Gefühle, wobei die Erfahrung nicht auf eine psychische Störung zurückgeführt wird, bzw. Verhalten, das Gereiztheit oder Ärger erkennen lässt. Es gibt einen allmählichen Übergang von Gefühlen, die nicht willkommen, aber ganz normal sind, zu Gefühlen, die den Patienten so beunruhigen, dass er professionelle Hilfe sucht', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P05', '1'::smallint, 'P', 'Senilität, sich alt fühlen/benehmen', 'R54', 'Sorgen wegen des Alterns *|||* Seneszenz', 'Demenz; P70', 'Vom Patienten als Folge einer emotionalen oder psychischen Erfahrung berichtete Gefühle, wobei die Erfahrung nicht auf eine psychische Störung zurückgeführt wird. Es gibt einen allmählichen Übergang von Gefühlen, die nicht willkommen, aber ganz normal sind, zu Gefühlen, die den Patienten so beunruhigen, dass er professionelle Hilfe sucht', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P06', '1'::smallint, 'P', 'Schlafstörungen', 'F51.0 *|||* F51.1 *|||* F51.2 *|||* F51.3 *|||* F51.4 *|||* F51.5 *|||* F51.8 *|||* F51.9 *|||* G47.0 *|||* G47.1 *|||* G47.2 *|||* G47.3 *|||* G47.4 *|||* G47.8 *|||* G47.9', 'Schlaflosigkeit *|||* Albträume *|||* Schlafapnoe *|||* Schlafwandeln *|||* Somnolenz', 'Jet lag; A88', 'Schlafstörung als Diagnose setzt voraus, dass das Schlafproblem eine ernsthafte Beschwerde darstellt, die nach übereinstimmender Meinung von Arzt und Patient nicht von einer anderen Störung verursacht wird, sondern eine eigenständige Beschwerde darstellt. Schlaflosigkeit setzt einen quantitativen oder qualitativen Mangel an Schlaf voraus, der nach Ansicht des Patienten unzufriedenstellend ist und sich über einen längeren Zeitraum erstreckt. Eine Hypersomnie liegt dann vor, wenn der Patient durch übertriebene Schläfrigkeit und Schlafanfälle während des Tages in der Ausübung seiner Funktion behindert wird', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P07', '1'::smallint, 'P', 'Vermindertes sexuelles Verlangen', 'F52.0', 'Frigidität *|||* Libidoverlust', 'nichtorganische Impotenz/Verlust der sexuellen Erfüllung; P08 *|||* Sorge wegen sexueller Präferenz; P09', 'Probleme hinsichtlich des sexuellen Verlangens, die nicht durch eine organische Störung oder eine Krankheit verursacht werden, sondern die Unfähigkeit des Patienten/der Patientin widerspiegeln, Partner in einer sexuellen Beziehung der von ihm/ihr gewünschten Art zu sein, weil das Verlangen nachgelassen hat oder die Genitalorgane nicht ausreichend reagieren bzw. funktionieren', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P08', '1'::smallint, 'P', 'Verminderte sexuelle Erfüllung', 'F52.1 *|||* F52.2 *|||* F52.3 *|||* F52.4 *|||* F52.5 *|||* F52.6 *|||* F52.7 *|||* F52.8 *|||* F52.9', 'nichtorganische Impotenz oder Dyspareunie *|||* Ejaculatio praecox *|||* Vaginismus psychogenen Ursprungs', 'Pobleme mit dem sexuellen Verlangen; P07 *|||* Sorge wegen sexueller Präferenz; P09 *|||* Vaginismus NNB; X04 *|||* organische Impotenz/sexuelle Pobleme; Y07', 'Probleme hinsichtlich des sexuellen Verlangens, die nicht durch eine organische Störung oder eine Krankheit verursacht werden, sondern die Unfähigkeit des Patienten/der Patientin widerspiegeln, Partner in einer sexuellen Beziehung der von ihm/ihr gewünschten Art zu sein, weil das Verlangen nachgelassen hat oder die Genitalorgane nicht ausreichend reagieren bzw. funktionieren, oder als Folge von Problemen mit der sexuellen Entwicklung', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P09', '1'::smallint, 'P', 'Besorgnis wegen sexueller Präferenz', 'F64.0 *|||* F64.1 *|||* F64.2 *|||* F64.8 *|||* F64.9 *|||* F65.0 *|||* F65.1 *|||* F65.2 *|||* F65.3 *|||* F65.4 *|||* F65.5 *|||* F65.6 *|||* F65.8 *|||* F65.9 *|||* F66.0 *|||* F66.1 *|||* F66.2 *|||* F66.8 *|||* F66.9', NULL, 'vermindertes sexuelles Verlangen; P07 *|||* verminderte sexuelle Erfüllung; P08', 'Probleme hinsichtlich des sexuellen Verlangens, die nicht durch eine organische Störung oder eine Krankheit verursacht werden, sondern die Unfähigkeit des Patienten/der Patientin widerspiegeln, Partner in einer sexuellen Beziehung der von ihm/ihr gewünschten Art zu sein, weil er/sie Probleme hinsichtlich seiner/ihrer sexuellen Identität/Neigung/Orientierung hat', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P10', '1'::smallint, 'P', 'Stammeln/Stottern/Tic', 'F95.0 *|||* F95.1 *|||* F95.2 *|||* F95.8 *|||* F95.9 *|||* F98.4 *|||* F98.5 *|||* F98.6', NULL, 'tic douloureux; N92', 'Stammeln/Stottern: eine Sprachstörung, die durch häufige Wiederholung und Verlängerung von Lauten gekennzeichnet ist, oder durch häufiges Ausdehnen von Pausen, die den Redefluß unterbrechen', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P11', '1'::smallint, 'P', 'Eßstörung beim Kind', 'F98.2 *|||* F98.3', 'Probleme beim Füttern *|||* Probleme mit dem Eßverhalten von Kindern', 'Anorexia nervosa; P86 *|||* Eßstörung beim Erwachsenen; T05', NULL, 'Verhaltensprobleme bei Kindern sind besonders schwer zu klassifizieren, was dadurch illustriert wird, dass sie auf vier Kapitel des ICPC verteilt sind. Ob Eltern diese Probleme ihrem Hausarzt berichten oder nicht, wird ihren Eindruck wiederspiegeln, ob es sich um ''normales'', wenn auch eventuell störendes Verhalten, oder um besorgniserregendes oder ''krankhaftes'' Verhalten handelt', NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P12', '1'::smallint, 'P', 'Bettnässen/Enuresis', 'F98.0', NULL, 'durch organische Störung verursachtes Bettnässen; U04', 'Unwillkürliches Urinieren bei Tag oder Nacht, das sich nicht auf eine organische Störung zurückführen lässt', 'Verhaltensprobleme bei Kindern sind besonders schwer zu klassifizieren, was dadurch illustriert wird, dass sie auf vier Kapitel des ICPC verteilt sind. Ob Eltern diese Probleme ihrem Hausarzt berichten oder nicht, wird ihren Eindruck wiederspiegeln, ob es sich um ''normales'', wenn auch eventuell störendes Verhalten, oder um besorgniserregendes oder ''krankhaftes'' Verhalten handelt', NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P13', '1'::smallint, 'P', 'Einkoten/Darmkontrollproblem', 'F98.1', NULL, NULL, 'Die Diagnose Enkompresis setzt voraus, dass wiederholt gewöhnlich wohlgeformte Fäkalien an unangebrachten Orten abgesetzt werden, was als abnormes Verhalten bezogen auf das Alter des Kindes angesehen wird und nicht auf Verstopfung, eine Störung des Schließmuskels oder eine andere Krankheit verursacht wird', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P15', '1'::smallint, 'P', 'chronischer Alkoholmissbrauch', 'F10.1 *|||* F10.2 *|||* F10.3 *|||* F10.4 *|||* F10.5 *|||* F10.6 *|||* F10.7 *|||* F10.8 *|||* F10.9 *|||* G31.2', 'alkoholisches Hirnsyndrom *|||* Alkoholpsychose *|||* Alkoholismus *|||* Delirium tremens', NULL, 'Eine durch Alkoholgenuss verursachte Störung, die zu mindestens einem der folgenden Symptome führt: übermäßiger Gebrauch mit klinisch signifikantem Schaden an der Gesundheit, ein Abhängigkeitssyndrom, Entzugserscheinungen, ein psychotisches Erscheinungsbild', 'Bei Problemen durch Substanzmißbrauch sollten die erheblichen Unterschiede zwischen Ländern und Kulturen berücksichtigt werden. Der Arzt kann auch ohne Zustimmung des Patienten einen Fall als ''chronischen Alkoholmißbrauch'' klassifizieren, also auch ohne die Bereitschaft des Patienten, therapeutischen Maßnahmen zuzustimmen', NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P16', '1'::smallint, 'P', 'akuter Alkoholmissbrauch', 'F10.0', 'Trunkenheit', NULL, 'Eine Störung, die auf Alkoholgenuß mit akuter Intoxikation zurückzuführen ist, mit oder ohne Vorgeschichte von chronischem Alkohomißbrauch', 'Ein Arzt kann entscheiden, eine Episode als ''akuten Alkoholmißbrauch'' zu bezeichnen, ohne Zustimmung des Patienten und folglich ohne die Bereitschaft des Patienten, einer medizinischen Behandlung zuzustimmen.', NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P17', '1'::smallint, 'P', 'Tabakmissbrauch', 'F17.0 *|||* F17.1 *|||* F17.2 *|||* F17.3 *|||* F17.4 *|||* F17.5 *|||* F17.6 *|||* F17.7 *|||* F17.8 *|||* F17.9', 'Übermäßiges Rauchen', NULL, 'Eine Störung, die auf Tabakgenuß zurückzuführen ist und zu mindestens einer der folgenden Erscheinungen führt: akute Intoxikation, übermäßiger Gebrauch mit klinisch signifikanter Schädigung der Gesundheit, Abhängigkeitssyndrom oder Entzugserscheinungen', 'Bei Problemen durch Substanzmißbrauch sollten die erheblichen Unterschiede zwischen Ländern und Kulturen berücksichtigt werden. Der Arzt kann auch ohne Zustimmung des Patienten einen Fall als ''chronischen Alkoholmißbrauch'' klassifizieren, also auch ohne die Bereitschaft des Patienten, therapeutischen Maßnahmen zuzustimmen', 'Risikofaktor NNB; A23');
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P18', '1'::smallint, 'P', 'Medikamentenmissbrauch', 'F13.0 *|||* F13.1 *|||* F13.2 *|||* F13.3 *|||* F13.4 *|||* F13.5 *|||* F13.6 *|||* F13.7 *|||* F13.8 *|||* F13.9 *|||* F19.0 *|||* F19.1 *|||* F19.2 *|||* F19.3 *|||* F19.4 *|||* F19.5 *|||* F19.6 *|||* F19.7 *|||* F19.8 *|||* F19.9 *|||* F55.0 *|||* F55.1 *|||* F55.2 *|||* F55.3 *|||* F55.4 *|||* F55.5 *|||* F55.6 *|||* F55.8 *|||* F55.9', 'Mißbrauch einer verschreibungspflichtigen Substanz', NULL, NULL, 'Bei Problemen durch Substanzmißbrauch sollten die erheblichen Unterschiede zwischen Ländern und Kulturen berücksichtigt werden. Der Arzt kann auch ohne Zustimmung des Patienten einen Fall als ''chronischen Alkoholmißbrauch'' klassifizieren, also auch ohne die Bereitschaft des Patienten, therapeutischen Maßnahmen zuzustimmen', NULL);
