INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P70', '7'::smallint, 'P', 'Demenz', 'F00.0 *|||* F00.1 *|||* F00.2 *|||* F00.9 *|||* F01.0 *|||* F01.1 *|||* F01.2 *|||* F01.3 *|||* F01.8 *|||* F01.9 *|||* F02.0 *|||* F02.1 *|||* F02.2 *|||* F02.3 *|||* F02.4 *|||* F02.8 *|||* F03 *|||* G30.0 *|||* G30.1 *|||* G30.8 *|||* G30.9', 'Alzheimer-Krankheit *|||* senile Demenz', NULL, 'Ein Syndrom durch eine Hirnerkrankung, üblicherweise chronischer/progredienter Natur, mit klinisch bedeutsamer mehrfacher Störung höherer kortikaler Funktionen (Gedächtnis, Denken, Orientatierung, Verständnis), bei erhaltenem Bewußtsein', NULL, 'Senilität; P05 *|||* andere psychologische Symptome; P29');
