INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P72', '7'::smallint, 'P', 'Schizophrenie', 'F20.0 *|||* F20.1 *|||* F20.2 *|||* F20.3 *|||* F20.4 *|||* F20.5 *|||* F20.6 *|||* F20.8 *|||* F20.9 *|||* F21 *|||* F22.0 *|||* F22.8 *|||* F22.9 *|||* F24 *|||* F25.0 *|||* F25.1 *|||* F25.2 *|||* F25.8 *|||* F25.9 *|||* F28 *|||* F29', 'alle Formen der Schizophrenie *|||* Paranoia', 'akute/vorübergehende Psychose; P98', 'grundsätzliche und charakteristische Denkstörungen, Wahrnehmung und Affekte , die inadäquat oder verzerrt sind (z.B. Gedankenlautwerden, Gedankeneingebung, Gedankenentzug, Wahnwahrnehmung, Stimmhalluzinationen, Kontrollwahn), bei klarem Bewußtsein und ohne Einschränkung der intellektuellen Fähigkeit.', NULL, 'Psychose NNB; P98');
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P73', '7'::smallint, 'P', 'Affektive Psychose', 'F30.0 *|||* F30.1 *|||* F30.2 *|||* F30.8 *|||* F30.9 *|||* F31.0 *|||* F31.1 *|||* F31.2 *|||* F31.3 *|||* F31.4 *|||* F31.5 *|||* F31.6 *|||* F31.7 *|||* F31.8 *|||* F31.9 *|||* F34.0', 'bipolare Störung *|||* Hypomanie *|||* Manie *|||* manisch-depressive Störung', 'Depression; P76', 'Eine grundlegende Störung im Bereich  der Affekte und Stimmungen, mit Wechsel zwischen euphorischen und deprimierten Zuständen (mit und ohne Angstgefühle). Bei einer manischen Störung sind Stimmung, Energie und Aktivität zugleich erhöht. Bei einer bipolaren Störung treten zumindest zwei Perioden von Stimmungsstörungen auf, mit Wechsel von hoch zu niedrig.', NULL, 'Psychose NNB; P98');
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P74', '7'::smallint, 'P', 'Angstörung/Panikattacke', 'F41.0 *|||* F41.1 *|||* F41.3 *|||* F41.8 *|||* F41.9', 'Angstneurose *|||* Panikstörung', 'Angsstörung mit Depression; P76 *|||* Angst NNB; P01', 'Klinisch bedeutsame Angststörung, die nicht auf eine bestimmte Umweltsituation beschränkt ist. Sie manifestiert sich als Panikattacken (wiederholte Anfälle schwerer Angst , die nicht auf eine bestimmte Situation beschränkt ist, mit oder ohne körperliche Symptome) oder als eine Störung mit generalisierter und andauernder Angst, die nicht auf eine bestimmte Situation bezogen ist und mit unterschiedlichen körperlichen Symptomen auftritt', NULL, 'Gefühl von Angst/Nervosität/Anspannung; P01');
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P75', '7'::smallint, 'P', 'Somatisierungsstörung', 'F44.0 *|||* F44.1 *|||* F44.2 *|||* F44.3 *|||* F44.4 *|||* F44.5 *|||* F44.6 *|||* F44.7 *|||* F44.8- *|||* F44.80 *|||* F44.81 *|||* F44.82 *|||* F44.88 *|||* F44.9 *|||* F45.0 *|||* F45.1 *|||* F45.2', 'Konversionsstörung *|||* hypochondrische Störung *|||* Hysterie *|||* Scheinschwangerschaft', NULL, 'Die Somatisierungs-/Konversionsstörung ist gekennzeichnet durch übermäßige Beschäftigung mit und wiederholtem Auftreten von körperlichen Symptomen und Beschwerden, zusammen mit fortwährender Einforderung medizinischer Untersuchung trotz wiederholter unauffälliger Befunde und beruhigender Versicherung der Ärzte.  Um diese Diagnose zu stellen, ist die vom Patienten vorgebrachte Darstellung von mehrfachen, immer wieder auftretenden und häufig wechselnden körperlichen Symptomen gegenüber dem Hausarzt über einen Zeitraum von mindestens einem Jahr erforderlich. Hypochondire setzt eine andauernde Beunruhigung voraus, die sich entweder  auf die äußere Erscheinung oder auf die Möglichkeit, eine ernste Krankheit zu haben, bezieht, verbunden mit ständigen somatischen Beschwerden über einen Zeitraum von mindestens einem Jahr, trotz wiederholter unauffälliger Befunde und beruhigender Versicherung der Ärzte.', 'Somatisierung ist das wiederholte Vorbringen körperlicher Symptome und Beschwerden, die auf körperliche Störungen schließen lassen, für die keine nachweisbaren organischen Befunde vorliegen oder physiologischen Mechanismen erkenbar sind, wobei es positive Hinweise darauf gibt, daß sie mit psychischen Faktoren zusammenhängen, während der Patient nicht das Gefühl hat, beim Umgang mit diesen psychischen Faktoren die Hervorbringung der Symptome zu kontrollieren. Psychische Symptome und Beschwerden einschließlich Schmerzen, die vom Patienten als Folge einer körperlichen Störung eine Systems bzw. Organs unter Kontrolle des autonomen Nervensystems dargestellt werden, oder aus andauernden, schweren/quälenden Schmerzen bestehen, die nicht als Folge eines physiologischen Vorgangs bzw. einer physiologischen Störung erklärt werden können, sind unter dem Symptom/der Beschwerde zu kodieren, die den körperlichen Aspekt repräsentiert, und - wenn möglich - mit einem Code, der das emotionelle oder psychische Problem darstellt, mit dem sie verbunden sind. Die Definition der Konversionsstörung in der ICD-10 (mindestens zwei Jahre) ist zu streng für den Gebrauch in der hausärztlichen Praxis', NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P76', '7'::smallint, 'P', 'Depressive Störung', 'F32.0 *|||* F32.1 *|||* F32.2 *|||* F32.3 *|||* F32.8 *|||* F32.9 *|||* F33.0 *|||* F33.1 *|||* F33.2 *|||* F33.3 *|||* F33.4 *|||* F33.8 *|||* F33.9 *|||* F34.1 *|||* F34.8 *|||* F34.9 *|||* F38.0 *|||* F38.1 *|||* F38.8 *|||* F39 *|||* F41.2 *|||* F53.0', 'depressive Neurose/Psychose *|||* gemischte Angst und Depression *|||* reaktive Depression *|||* postnatale/Wochenbettdepression', 'akute Stressreaktion; P02', 'eine grundlegende Störung in Affekt und Stimmung in Richtung Depression. Stimmung und Aktivität sind zugleich herabgesetzt, dazu kommt eine Einschränkung der Fähigkeit, Freude, Interesse und Konzentration aufzubringen. Gewöhnlich treten Schlaf- und Appetitstörungen auf, Selbstwertgefühl und Selbstsicherheit sind herabgesetzt.', NULL, 'depressives Gefühl; P03');
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P77', '7'::smallint, 'P', 'Suizid/Suizidversuch', 'F99', 'parasuizidale Handlung *|||* erfolgreicher Suizidversuch (mit A96 doppelt kodieren)', 'Angst vor Suizid; P27', NULL, NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P78', '7'::smallint, 'P', 'Neurasthenie/Erschöpfung/Burnout', 'F48.0', NULL, NULL, 'Erhöhte Ermüdbarkeit mit unangenehmen Empfindungen, Konzentrationsschwäche und einem ständigen Abnehmen der Leistungsfähigkeit und Lebenstüchtigkeit; ein Gefühl der körperlichen Schwäche und Erschöpfung nach geistiger Anstrengung oder nach geringer körperlicher Anstrengung, das oft von Muskelschmerzen und der Unfähigkeit, zu entspannen begleitet ist', NULL, 'Abgeschlagenheit/postvirales Ermüdungssyndrom/chronic fatigue syndrome; A04');
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P79', '7'::smallint, 'P', 'Phobie/Zwangsstörung', 'F40.0- *|||* F40.00 *|||* F40.01 *|||* F40.1 *|||* F40.2 *|||* F40.8 *|||* F40.9 *|||* F42.0 *|||* F42.1 *|||* F42.2 *|||* F42.8 *|||* F42.9', NULL, NULL, 'die phobische Angststörung setzt voraus: Äußerung deutlicher Ängstlichkeit, die in genau definierten Situationen entsteht, die gewöhnlich nicht als gefährlich angesehen werden; der Patient versucht ie Situationen zu vermeiden oder durchlebt sie mit Angstgefühlen. Die Diagnose Zwangsstörung setzt voraus: das Vorhandensein von quälenden und immer wiederkehrenden Zwangsgedanken oder Zwangshandlungen, die vom Patienten als von ihm selbst stammend erkannt werden; stereotype Zwangshandlungen werden ständig wiederholt, um ein objektiv unwahrscheinliches Ereignis zu verhindern, obwohl sie vom Patienten als sinn- und wirkungslos erkannt werden', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P80', '7'::smallint, 'P', 'Persönlichkeitsstörung', 'F60.0 *|||* F60.1 *|||* F60.2 *|||* F60.3- *|||* F60.30 *|||* F60.31 *|||* F60.4 *|||* F60.5 *|||* F60.6 *|||* F60.7 *|||* F60.8 *|||* F60.9 *|||* F61 *|||* F62.0 *|||* F62.1 *|||* F62.8- *|||* F62.80 *|||* F62.88 *|||* F62.9 *|||* F63.0 *|||* F63.1 *|||* F63.2 *|||* F63.8 *|||* F63.9 *|||* F68.0 *|||* F68.1 *|||* F68.8 *|||* F69', 'Psychopathie *|||* Kompensationsneurose *|||* Münchhausen Syndrom *|||* Verhaltensstörung beim Erwachsenen', NULL, 'anhaltende und klinisch bedeutsame Zustände und Verhaltensmuster im persönlichen Lebensstil bzw. in den Beziehungen zu anderen, die signifikante oder extreme Abweichungen von dem anzeigen, was in einem bestimmten Kulturkreis als Norm für Wahrnehmung, Gefühle und Verhalten eines durchschnittlichen Individuums gilt. Diese Muster sind tief verwurzelt und dauerhaft.', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P81', '7'::smallint, 'P', 'Hyperkinesie', 'F90.0 *|||* F90.1 *|||* F90.8 *|||* F90.9', 'Aufmerksamkeitsstörung (ADD) *|||* Hyperaktivität', 'hyperkinetische Störung mit Beginn in der Pubertät; P23 *|||* Lernstörung; P24', 'Frühes Auftreten eine Mangels an Ausdauer in Aktivitäten, die kognitive Beteiligung erfordern, mit der Tendenz, von einer Aktivität zur nächsten zu wechseln, ohne eine davon zu vollenden, mit unorganisiertem und schlecht geregeltem Verhalten und übermäßiger Aktivität', NULL, 'hyperaktives Kind; P22');
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P82', '7'::smallint, 'P', 'Posttraumatische Belastungsstörung', 'F43.1', 'persistierende Anpassungsstörung', NULL, 'ein Stress verursachendes Ereignis, dem ein bedeutender Zustand von Unruhe und Erregung folgt, mit verzögerten oder verlängerten Reaktionen, Nachhallerinnerungen, Alpträumen, emotionaler Stumpfheit, und Anhedonie, die sich störend auf die sozialen Beziehungen und die Leistungsfähigkeit auswirken und mit dauerhaften Depressionen, Beklemmungen, Besorgnis und dem Gefühl, mit der Umwelt nicht zurechtzukommen, verbunden sind', NULL, 'Angstgefühl; P01 *|||* akute Stressreaktion; P02 *|||* depressives Gefühl; P03');
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P85', '7'::smallint, 'P', 'Mentale Retardierung', 'F70.0 *|||* F70.1 *|||* F70.8 *|||* F70.9 *|||* F71.0 *|||* F71.1 *|||* F71.8 *|||* F71.9 *|||* F72.0 *|||* F72.1 *|||* F72.8 *|||* F72.9 *|||* F73.0 *|||* F73.1 *|||* F73.8 *|||* F73.9 *|||* F78.0 *|||* F78.1 *|||* F78.8 *|||* F78.9 *|||* F79.0 *|||* F79.1 *|||* F79.8 *|||* F79.9', NULL, 'mentale Retardierung durch angeborene Fehlbildung; A90', 'Mangelnde oder unvollständige Entfaltung der geistigen Fähigkeiten mit Beeinträchtigung der Fertigkeiten während der Entwicklungsphase und ein niedriges allgemeines Intelligenzniveau, mit oder ohne Beeinträchtigung der Fertigkeiten', NULL, NULL);
INSERT INTO staging.icpc (icpc, component, chapter, term, icd10, incl, excl, crit, notes, see_also) VALUES ('P86', '7'::smallint, 'P', 'Anorexia nervosa/Bulämie', 'F50.0 *|||* F50.1 *|||* F50.2 *|||* F50.3 *|||* F50.4', NULL, NULL, 'Anorexia nervosa: vom Patienten bewußt herbeigeführter und unterhaltener Gewichtsverlust, verbunden mit intensiver und überbewerteter Furcht vor Übergewicht sowie vor Körperschemastörung. Bulimie: Wiederholte Anfälle von übermäßigem Essen und übertriebener Sorge um das Körpergewicht, die zu einem Verhaltensmuster führen, bei dem auf das Überessen ein absichtlich herbeigeführtes Erbrechen oder die Anwendung von Abführmitteln folgt', NULL, 'Eßstörung, Nahrungsverweigerung P11,; P29 *|||* Problem beim Füttern T04,; T05');
