    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // Add your handling code here:
        try {
            String query = (String) jComboBox1.getSelectedItem();
            String var = (String) jComboBox2.getSelectedItem();
            System.out.print("query is ");        System.out.print(query);
            System.out.print(" : var is ");        System.out.println(var);
            
            java.util.List l = manager.doQuery(query, var, true);
            for (java.util.Iterator i = l.iterator();  i.hasNext(); ) {
                Object o = null;
               try {
                    o = i.next();
                    System.out.print("*****ROOT OBJECT:");
                    IntrospectingPrinter.instance().printObject(System.out , o);
               } catch (Exception e) {
                   System.out.println( "WITH " + o + " got error " + e);
              }
            }
        } catch (Exception e) {
            e.printStackTrace();
        }
        
    }//GEN-LAST:event_jButton1ActionPerformed
    
